			["customText"] = "function()\n    \n    --@Author: Krazyito-Mal'ganis, US\n    --@Version: v4 - Timer only\n    \n    if aura_env.Encounter_Start == true then\n        \n        local resCharges, _, started, duration = GetSpellCharges(20484) -- Takes the charges and timer of 'Rebirth'\n        local timerString = \"\" -- varibale to store the timer in.\n        \n        \n        if started then\n            local time = duration - (GetTime() - started)\n            local m = floor(time/60)\n            local s = mod(time, 60)\n            timerString =  string.format(\"%d:%02d\", m, s)\n        end\n        \n        if resCharges == nil then -- if rebirth has 'nil' charges, then we are not in a boss encounter. Set it to 0 to avoid errors.\n            resCharges = 0\n        end\n        \n        if resCharges == 0 then -- Aesthetics changes (a red 0, white otherwise)\n            resCharges = \"|cFFFF0001\" .. resCharges .. \"|r\"\n        else \n            resCharges =\"|cFFFFFFFF\" .. resCharges .. \"|r\" \n        end\n        \n        \n        return resCharges .. \"       \" .. timerString .. \"\\n\"  -- Output the information.\n        \n    else\n        return \"1     Test Text!\" -- This is here to avoid errors.\n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
