				["events"] = "COMBAT_LOG_EVENT_UNFILTERED",
				["customDuration"] = "function()\n    if aura_env.add_Mad ~=nil \n    then\n        \n        if aura_env.stTime and aura_env.edTime\n        then\n            adding_cast_Mad = aura_env.add_Mad*(GetTime()*1000 -aura_env.stTime)/(aura_env.edTime-aura_env.stTime)\n            -- adding_cast_AP = aura_env.stTime\n        end\n        \n        local insanPosition = UnitPower('player',SPELL_POWER_INSANITY)+aura_env.add_Mad;\n        local insanMaxPosition = UnitPowerMax(\"player\", SPELL_POWER_INSANITY);\n        if insanPosition>insanMaxPosition\n        then\n            return insanMaxPosition, insanMaxPosition, 0,0 \n        else\n            return insanPosition, insanMaxPosition, 0,0\n        end\n    else\n        return 0, insanMaxPosition, 0,0 \n    end\nend",
