				["customIcon"] = "function()\n    if aura_env.position then\n        return string.format(\"Interface\\\\TargetingFrame\\\\UI-RaidTargetingIcon_%d\", aura_env.position)\n    else\n        return \"Interface\\\\Icons\\\\ability_bossmannoroth_empoweredmannorothsgaze\"\n    end \nend\n\n\n\n",
