				["eclipsetype"] = "moon",
				["event"] = "Power",
				["custom"] = "function()\n    \n    local SWV_Mad\n    local mblast_Mad\n    \n    -- Первая часть, тут проверяем модификаторы накопления АП\n    \n    local fortmind=0\n    local pi=0\n    local surrender=0\n    \n    local SpecIndex = GetActiveSpecGroup();\n    local _, _, _, TalentIsSelected = GetTalentInfo(1,2,SpecIndex)\n    if  TalentIsSelected\n    then\n        fortmind = 1\n    end\n    \n    for i=1,40 do \n        local _, _, _, _, _, _, _, _, _, _, spellId=UnitBuff(\"player\",i); \n        -- print(spellId)\n        if spellId == 10060\n        then \n            pi = 1\n        elseif spellId == 193223\n        then \n            surrender = 1\n        end\n    end\n    \n    -- comment format: (fortmind, pi, surrender)\n    \n    if fortmind == 1             \n    then\n        eluna = 0\n        if pi ==1 \n        then\n            pi =0\n            if surrender == 1\n            then            -- (1,1,1)\n                surrender = 0\n                mblast_Mad = 54   --?\n            else             -- (1,1,0)\n                mblast_Mad = 18   --?\n            end\n        else\n            if surrender == 1\n            then             -- (1,0,1)\n                surrender = 0\n                mblast_Mad = 43  --?\n            else              -- (1,0,0)\n                mblast_Mad = 14   \n            end\n        end\n    else\n        if pi ==1 \n        then\n            pi =0\n            if surrender == 1\n            then             -- (0,1,1)\n                surrender = 0\n                SWV_Mad = 94 --?\n                mblast_Mad = 45 --?\n            else              -- (0,1,0)\n                SWV_Mad = 31\n                mblast_Mad = 15\n            end\n        else\n            if surrender == 1\n            then              -- (0,0,1)\n                surrender = 0\n                SWV_Mad = 75 \n                mblast_Mad = 36 \n            else              -- (0,0,0)\n                SWV_Mad = 25\n                mblast_Mad = 12\n            end\n        end\n    end\n    \n    \n    \n    -- Часть вторая, проверяем, кастует ли персонаж, если да, то генерирующий ли АП спелл\n    \n    local spellName, _, _, _, startTime, endTime = UnitCastingInfo(\"player\")\n    \n    if spellName then \n        local _,_, _, _, _, _, spellID = GetSpellInfo(spellName)\n        aura_env.stTime = startTime\n        aura_env.edTime = endTime\n        -- Перечисляем генераторы АП и количество генерируемого АП\n        if(spellID == 205351) \n        then \n            aura_env.add_Mad=SWV_Mad\n            --    print(aura_env.add_AP)\n        elseif(spellID == 8092) \n        then \n            aura_env.add_Mad=mblast_Mad\n            --    print(aura_env.add_AP)\n        else \n            aura_env.add_Mad=0\n        end\n    else\n        aura_env.add_Mad=0\n    end\n    \n    \n    \n    return true\nend",
