				["lunar_power_operator"] = ">=",
				["custom_type"] = "status",
				["customDuration"] = "function()\n    if aura_env.add_AP ~=nil \n    then\n        local WA_adding_BALANCE_cast_eng\n        local adding = 0\n        if not aura_env.minus\n        then\n            adding = aura_env.add_AP\n        end\n        \n        local eclipsePosition = UnitPower('player',SPELL_POWER_LUNAR_POWER)+adding;\n        local eclipseMaxPosition = UnitPowerMax(\"player\", SPELL_POWER_LUNAR_POWER);\n        \n        if aura_env.stTime and aura_env.edTime\n        then\n            WA_adding_BALANCE_cast_eng = aura_env.add_AP*(GetTime()*1000 -aura_env.stTime)/(aura_env.edTime-aura_env.stTime)\n        end\n        \n        if aura_env.edTime ~= nil\n        then\n            aura_env.saveEpositionOnPing =  eclipsePosition\n            aura_env.saveTpositionOnPing = GetTime()\n        end\n        \n        if aura_env.edTime == nil and aura_env.saveTpositionOnPing and aura_env.saveTpositionOnPing >0\n        then\n            if aura_env.myApprPing+aura_env.saveTpositionOnPing > GetTime()\n            then\n                eclipsePosition = aura_env.saveEpositionOnPing\n                --WA_adding_BALANCE_cast_eng = aura_env.saveEpositionOnPing-UnitPower('player',SPELL_POWER_LUNAR_POWER)\n            end\n        end\n        \n        WeakAuras.ScanEvents(\"WA_event_engUpdate_by_Afreg\", WA_adding_BALANCE_cast_eng)\n        \n        if eclipsePosition>eclipseMaxPosition\n        then\n            return eclipseMaxPosition, eclipseMaxPosition, 0,0 \n        else\n            return eclipsePosition, eclipseMaxPosition, 0,0\n        end\n    else\n        return 0, eclipseMaxPosition, 0,0 \n    end\nend\n\n\n",
				["custom"] = "function()\n    aura_env.myApprPing  = 0.2                 -- Твой примерный пинг, можно больше\n    \n    local moon_AP\n    local sun_AP\n    local spell_1_AP = -15\n    \n    aura_env.minus = false\n    \n    -- Первая часть, тут проверяем модификаторы накопления АП\n    \n    local MYtalent_1=false       -- eluna\n    local MYtalent_2=false       -- align\n    local MYtalent_3=false       -- incornation\n    \n    local MYtalent_1_mult = 1.25\n    local MYtalent_2_mult = 1.5\n    local MYtalent_3_mult = 1.5\n    local sum_mult = 1\n    \n    local talent_spName = GetSpellInfo(202737)\n    if UnitBuff('player', talent_spName)\n    then \n        MYtalent_1 = true\n    end\n    talent_spName = GetSpellInfo(194223)\n    if UnitBuff('player', talent_spName)\n    then \n        MYtalent_2 = true\n    end\n    talent_spName = GetSpellInfo(102560)\n    if UnitBuff('player', talent_spName)\n    then \n        MYtalent_3 = true\n    end\n    \n    --make multiplier on our buffs\n    if MYtalent_1 == true then\n        sum_mult = sum_mult*MYtalent_1_mult\n    end\n    if MYtalent_2 == true then\n        sum_mult = sum_mult*MYtalent_2_mult\n    end\n    if MYtalent_3 == true then\n        sum_mult = sum_mult*MYtalent_3_mult\n    end\n    \n    --find the real value of madness we get\n    moon_AP = 12*sum_mult\n    sun_AP = 8*sum_mult\n    \n    -- Часть вторая, проверяем, кастует ли персонаж, если да, то генерирующий ли АП спелл\n    \n    local spellName, _, _, _, startTime, endTime = UnitCastingInfo(\"player\")\n    \n    if spellName then \n        local _,_, _, _, _, _, spellID = GetSpellInfo(spellName)\n        aura_env.stTime = startTime\n        aura_env.edTime = endTime\n        -- Перечисляем генераторы АП и количество генерируемого АП\n        if(spellID == 190984) \n        then \n            aura_env.add_AP=sun_AP\n        elseif(spellID == 194153) \n        then \n            aura_env.add_AP=moon_AP\n        elseif(spellID == 202767)\n        then\n            aura_env.add_AP=10\n        elseif(spellName == GetSpellInfo(202768))\n        then\n            aura_env.add_AP=20\n        elseif(spellName == GetSpellInfo(202771))\n        then\n            aura_env.add_AP=40\n        elseif(spellID ==202347)\n        then\n            aura_env.minus = true\n            aura_env.add_AP = spell_1_AP\n        else \n            aura_env.add_AP=0\n        end\n    else\n        aura_env.stTime = nil\n        aura_env.edTime = nil\n        aura_env.add_AP=0\n    end\n    \n    -- Хрень с учетом пинга\n    \n    if aura_env.saveEpositionOnPing == nil\n    then \n        aura_env.saveEpositionOnPing = 0\n        aura_env.saveTpositionOnPing =0\n    end\n    \n    return true\nend",
