				["customDuration"] = "function()\n    local myEnergyType = 'SPELL_POWER_MAELSTROM'              -- сюда свой тип энергии\n    if aura_env.add_eng ~=nil \n    then\n        local WA_adding_ELENCH_cast_eng\n        local isSpiritWolf = 0\n        for i = 1,40 do                  --здесь проверка на бафф призрачного волка\n            local _, _, _, _, _, _, _, _, _,_, spellId = UnitBuff(\"player\", i)\n            if spellId == 2645 \n            then \n                isSpiritWolf = 1\n                break\n            end\n        end\n        \n        if isSpiritWolf == 0\n        then\n            \n            local energy_Position = UnitPower('player',myEnergyType)+aura_env.add_eng;\n            local energy_MaxPosition = UnitPowerMax(\"player\", myEnergyType);\n            \n            if aura_env.stTime and aura_env.edTime\n            then\n                WA_adding_ELENCH_cast_eng = aura_env.add_eng*(GetTime()*1000 -aura_env.stTime)/(aura_env.edTime-aura_env.stTime)\n            end\n            \n            if aura_env.edTime ~= nil\n            then\n                aura_env.saveEpositionOnPing =  energy_Position\n                aura_env.saveTpositionOnPing = GetTime()\n            end\n            \n            if aura_env.edTime == nil and aura_env.saveTpositionOnPing and aura_env.saveTpositionOnPing >0\n            then\n                if aura_env.myApprPing+aura_env.saveTpositionOnPing > GetTime()\n                then\n                    energy_Position = aura_env.saveEpositionOnPing\n                    WA_adding_ELENCH_cast_eng = aura_env.saveEpositionOnPing-UnitPower('player',myEnergyType)\n                end\n            end\n            \n            WeakAuras.ScanEvents(\"WA_event_engUpdate_by_Afreg\", WA_adding_ELENCH_cast_eng)\n            \n            aura_env.SpWolfCur = energy_Position\n            aura_env.SpWolfMax = energy_MaxPosition\n            \n            if energy_Position>energy_MaxPosition\n            then\n                return energy_MaxPosition, energy_MaxPosition, 0,0 \n            else\n                return energy_Position, energy_MaxPosition, 0,0\n            end\n        else\n            if aura_env.SpWolfCur \n            then\n                return aura_env.SpWolfCur, aura_env.SpWolfMax, 0,0\n            else\n                return 0, 1, 0,0\n            end\n        end\n        \n    else\n        return 0, energy_MaxPosition, 0,0 \n    end\n    \nend",
