			["displayText"] = "%c",
			["customText"] = "function ()\n    \n    if (_raidCDs_groupPoll == nil) then _raidCDs_groupPoll = GetTime() end        \n    if (GetTime() - _raidCDs_groupPoll < 1.5) then return \"\" end\n    \n    _raidCDs_groupPoll = GetTime();\n    \n    if (InspectFrame ~= nil and InspectFrame:IsShown()) then return \"\" end\n    \n    if (_raidCDs_groupPoll_state == nil) then _raidCDs_groupPoll_state = \"nextPlayer\" end\n    if (_raidCDs_groupPoll_playerCounter == nil) then _raidCDs_groupPoll_playerCounter = 0 end\n    \n    local playerCount = GetNumGroupMembers();\n    local _, _, _, _, maxPlayers = GetInstanceInfo();\n    local _, instanceType = IsInInstance();\n    \n    if (instanceType ~= \"none\") then\n        playerCount = math.min(playerCount, maxPlayers);\n    end    \n    \n    if (_raidCDs_groupPoll_state==\"inspectPlayer\") then        \n        local playerName = _raidCDs_groupPoll_currentPlayer;\n        \n        if (not UnitInParty(playerName)) then\n            _raidCDs_groupPoll_state = \"nextPlayer\";            \n            return \"\";\n        end\n        \n        local isPlayer = (playerName == UnitName(\"player\"));\n        local spec;\n        local specName;        \n        \n        if (isPlayer) then         \n            spec = GetSpecialization();          \n        else\n            spec = GetInspectSpecialization(playerName);      \n        end        \n        \n        if (spec == nil) then\n            _raidCDs_groupPoll_state = \"nextPlayer\";\n            return \"\";\n        end        \n        \n        if(isPlayer) then\n            _, specName = GetSpecializationInfo(spec);\n        else\n            local role = GetSpecializationRoleByID(spec);\n            if(role ~= nil) then\n                _, specName = GetSpecializationInfoByID(spec);\n            end            \n        end       \n        \n        local class = UnitClass(playerName); \n        local activeSpec  = GetActiveSpecGroup(not isPlayer);        \n        \n        if (specName == nil) then\n            _raidCDs_groupPoll_state = \"nextPlayer\";\n            return \"\";\n        end\n        \n        if (class == \"Warrior\") then\n            if (_raidCDs_warriors == nil) then _raidCDs_warriors = {} end\n            if (_raidCDs_warriors[playerName] == nil) then _raidCDs_warriors[playerName] = {} end\n            if (_externCDs_warriors == nil) then _externCDs_warriors = {} end\n            if (_externCDs_warriors[playerName] == nil) then _externCDs_warriors[playerName] = {} end\n            if (_interrupts_warriors == nil) then _interrupts_warriors = {} end\n            if (_interrupts_warriors[playerName] == nil) then _interrupts_warriors[playerName] = {} end\n            \n            if (_interrupts_warriors[playerName][\"Pummel\"] == nil) then\n                _interrupts_warriors[playerName][\"Pummel\"] = GetTime();\n            end\n            \n            if (specName == \"Protection\") then\n                local _, _, _, safeguardSpecced = GetTalentInfo(2, 3, activeSpec, not isPlayer, playerName);            \n                if (safeguardSpecced) then\n                    if (_externCDs_warriors[playerName][\"Safeguard\"] == nil) then\n                        _externCDs_warriors[playerName][\"Safeguard\"] = GetTime();\n                    end    \n                else\n                    _externCDs_warriors[playerName][\"Safeguard\"] = nil;\n                end              \n                _raidCDs_warriors[playerName][\"C-Shout\"] = nil;   \n            else             \n                if (_raidCDs_warriors[playerName][\"C-Shout\"] == nil) then\n                    _raidCDs_warriors[playerName][\"C-Shout\"] = GetTime();\n                end       \n                _externCDs_warriors[playerName][\"Safeguard\"] = nil;   \n            end\n            \n        elseif (class == \"Priest\") then\n            if (_raidCDs_priests == nil) then _raidCDs_priests = {} end\n            if (_raidCDs_priests[playerName] == nil) then _raidCDs_priests[playerName] = {} end\n            if (_externCDs_priests == nil) then _externCDs_priests = {} end\n            if (_externCDs_priests[playerName] == nil) then _externCDs_priests[playerName] = {} end\n            if (_interrupts_priests == nil) then _interrupts_priests = {} end\n            if (_interrupts_priests[playerName] == nil) then _interrupts_priests[playerName] = {} end\n            if (_utilityCDs_priests == nil) then _utilityCDs_priests = {} end\n            if (_utilityCDs_priests[playerName] == nil) then _utilityCDs_priests[playerName] = {} end                    \n            \n            if (specName == \"Holy\") then\n                local _, _, _, symbolOfHopeSpecced = GetTalentInfo(4, 3, activeSpec, not isPlayer, playerName);  \n                if (symbolOfHopeSpecced) then                \n                    if (_utilityCDs_priests[playerName][\"Hope\"] == nil) then\n                        _utilityCDs_priests[playerName][\"Hope\"] = GetTime();\n                    end                  \n                else\n                    _utilityCDs_priests[playerName][\"Hope\"] = nil;\n                end\n                if (_utilityCDs_priests[playerName][\"Grip\"] == nil) then\n                    _utilityCDs_priests[playerName][\"Grip\"] = GetTime();\n                end                                  \n                if (_raidCDs_priests[playerName][\"D-Hymn\"] == nil) then\n                    _raidCDs_priests[playerName][\"D-Hymn\"] = GetTime();\n                end    \n                if (_externCDs_priests[playerName][\"G-Spirit\"] == nil) then\n                    _externCDs_priests[playerName][\"G-Spirit\"] = GetTime();\n                end    \n                _interrupts_priests[playerName][\"Silence\"] = nil;\n                _raidCDs_priests[playerName][\"VE\"] = nil;  \n                _raidCDs_priests[playerName][\"Barrier\"] = nil;\n                _externCDs_priests[playerName][\"P-Sup\