				["customDuration"] = "function()\n    if aura_env.add_eng ~=nil \n    then\n        local  adding_cast_eng\n        \n        local myEnergyType = 'SPELL_POWER_INSANITY'              -- сюда свой тип энергии\n        \n        local energy_Position = UnitPower('player',myEnergyType)+aura_env.add_eng;\n        local energy_MaxPosition = UnitPowerMax(\"player\", myEnergyType);\n        \n        if aura_env.stTime and aura_env.edTime\n        then\n            adding_cast_eng = aura_env.add_eng*(GetTime()*1000 -aura_env.stTime)/(aura_env.edTime-aura_env.stTime)\n        else\n            adding_cast_eng = 0\n        end\n        \n        WeakAuras.ScanEvents(\"WA_event_engUpdate_by_Afreg\", adding_cast_eng)       \n        \n        if aura_env.edTime ~= nil\n        then\n            aura_env.saveEpositionOnPing =  energy_Position\n            aura_env.saveTpositionOnPing = GetTime()\n        end\n        \n        if aura_env.edTime == nil and aura_env.saveTpositionOnPing and aura_env.saveTpositionOnPing >0\n        then\n            if aura_env.myApprPing+aura_env.saveTpositionOnPing>GetTime()\n            then\n                energy_Position = aura_env.saveEpositionOnPing\n            end\n        end\n        \n        aura_env.my_eng = UnitPower('player',myEnergyType)\n        \n        return math.min(energy_Position, energy_MaxPosition) , energy_MaxPosition, 0,0 \n    else\n        return 0, energy_MaxPosition, 0,0 \n        \n    end\nend",
				["custom_type"] = "status",
				["custom"] = "function()\n    aura_env.myApprPing  = 0.2                 -- Твой примерный пинг, можно больше\n    \n    --Касты, генерирующие энергию, для каждого класса свои\n    \n    local MYspell_1                                           --SWV_Mad\n    local MYspell_2                                           --mblast_Mad\n    local MYspell_3                                           --vTouch_Mad\n    local MYspell_4                                           --mSpike_Mad\n    \n    -- Первая часть, тут проверяем модификаторы накопления энергии (таланты, бурсты, меняющие количество получаемой энергии со способности\n    -- Кастомна для каждого класса\n    \n    local MYtalent_1=0       -- fortmind\n    local MYtalent_2=0       -- PI\n    local MYtalent_3=0       -- surrender\n    \n    local SpecIndex = GetActiveSpecGroup();\n    local _, _, _, TalentIsSelected = GetTalentInfo(1,2,SpecIndex)\n    if  TalentIsSelected\n    then\n        MYtalent_1 = 1\n    end\n    \n    for i=1,40 do \n        local _, _, _, _, _, _, _, _, _, _, spellId=UnitBuff(\"player\",i); \n        -- print(spellId)\n        if spellId == 10060\n        then \n            MYtalent_2 = 1\n        elseif spellId == 193223\n        then \n            MYtalent_3 = 1\n        end\n    end\n    \n    -- comment format: (fortmind, pi, surrender)\n    \n    if  MYtalent_1 == 1             \n    then\n        MYtalent_1 = 0\n        if MYtalent_2 ==1 \n        then\n            MYtalent_2 =0\n            if MYtalent_3 == 1\n            then            -- (1,1,1)\n                MYtalent_3 = 0\n                MYspell_2 = 54   \n                MYspell_3 = 18\n                MYspell_4 = 18\n            else             -- (1,1,0)\n                MYspell_2 = 18   \n                MYspell_3 = 6\n                MYspell_4 = 6\n            end\n        else\n            if MYtalent_3 == 1\n            then             -- (1,0,1)\n                MYtalent_3 = 0\n                MYspell_2 = 43  \n                MYspell_3 = 14\n                MYspell_4 = 14\n            else              -- (1,0,0)\n                MYspell_2 = 14\n                MYspell_3 = 5\n                MYspell_4 = 5\n            end  \n        end\n    else\n        if MYtalent_2 ==1 \n        then\n            MYtalent_2 =0\n            if MYtalent_3 == 1 \n            then             -- (0,1,1)\n                MYtalent_3 = 0\n                MYspell_1 = 94 \n                MYspell_2 = 45 \n                MYspell_3 = 15\n                MYspell_4 = 15 \n            else              -- (0,1,0)\n                MYspell_1 = 31\n                MYspell_2 = 15\n                MYspell_3 = 5\n                MYspell_4 = 5\n            end\n        else\n            if MYtalent_3 == 1\n            then              -- (0,0,1)\n                MYtalent_3 = 0\n                MYspell_1 = 75 \n                MYspell_2 = 36 \n                MYspell_3 = 12\n                MYspell_4 = 12\n            else              -- (0,0,0)\n                MYspell_1 = 25\n                MYspell_2 = 12\n                MYspell_3 = 4\n                MYspell_4 = 4\n            end\n        end\n    end\n    \n    -- Їасть вторая, проверяем, кастует ли персонаж, если да, то генерирующий ли энергию спелл\n    \n    local spellName, _, _, _, startTime, endTime = UnitCastingInfo(\"player\")\n    \n    if spellName then \n        local _,_, _, _, _, _, spellID = GetSpellInfo(spellName)\n        aura_env.stTime = startTime\n        aura_env.edTime = endTime\n        -- Перечисляем генераторы энергии и количество генерируемой энергии\n        -- Для каждого класса перечисляем абилки самостоятельно\n        \n        if(spellID == 205351) \n        then \n            aura_env.add_eng=MYspell_1\n        elseif(spellID == 8092) \n        then \n            aura_env.add_eng=MYspell_2\n        elseif(spellID == 73510) \n        then \n            aura_env.add_eng=MYspell_3\n        elseif(spellID == 34914) \n        then \n            aura_env.add_eng=MYspell_4\n        else \n            aura_env.add_eng=0\n        end\n    else\n        aura_env.stTime = nil\n        aura_env.edTime = nil\n        aura_env.add_eng=0\n    end\n    \n    -- Хрень с учетом пинга\n    \n    if aura_env.saveEpositionOnPing == nil\n    then\n        aura_env.saveEpositionOnPing = 0\n        aura_env.saveTpositionOnPing =0\n    end\n    \n    return true\nend",
